import React from "react";

function Night ({sunrise,sunset,dateTimeZone,offset}){
    //Calculate Sunset UTC
    let sunsetTime = new Date(sunset * 1000);
    let utcSunset =
      sunsetTime.getTime() + sunsetTime.getTimezoneOffset() * 60000;
    let sunsetUTCnd = new Date(utcSunset + 3600000 * offset);

    //Calculate sunrise UTC
    let sunriseTime = new Date(sunrise * 1000);
    let utcSunrise =
      sunriseTime.getTime() + sunriseTime.getTimezoneOffset() * 60000;
    let sunriseUTCnd = new Date(utcSunrise + 3600000 * offset);

    //Make black on sunset and sunrise
    let sunsetUTCgetTime = sunsetUTCnd.getTime();
    let dateTimeZoneGetTime = dateTimeZone.getTime();
    if (dateTimeZoneGetTime >= sunsetUTCgetTime || dateTimeZoneGetTime<= sunriseUTCnd) {
      document.body.style.backgroundColor = "#2f3e46";
    }

    return(
        <>
        </>
    )
}

export default Night;
