import React, {useState, useEffect} from "react";
import CityInput from "./CityInput";
import DisplaySky from "./DisplaySky";
import '../css/App.css';


function App() {
  //Get city from Input
  const [city,setCity] = useState("Berlin");
  const [search,setSearch] = useState("Berlin");
  const [loading,setLoading] = useState(false)
  const [data, setData] = useState([]);


  function handleCityInput(e){
    setCity(e.target.value)
  }

  //Get info on enter
  function handleSubmitOnEnter(e){
    if (e.key === "Enter") {
      e.preventDefault();
      setSearch(city)
    }
  }

  //Fetch API
  const API_KEY = process.env.REACT_APP_API_KEY 
  const URI = `http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=${API_KEY}`;


  useEffect(() => {
      setLoading(true);
      const fetchData = async () => {
      try {
          const response = await fetch(URI);
          const results = await response.json();
          console.log(results);
          setData(results);
          setLoading(false);
      } catch (error) {
          setLoading(false)
          console.error(error.message);
      }
      };
      fetchData();
  }, [URI]);

  //Time
  const time = new Date()
  //TimeZone
  const utc = time.getTime() + time.getTimezoneOffset() * 60000;
  const offset = data.timezone / 3600;
  const dateTimeZone = new Date(utc + 3600000 * offset);
  //Format Clock
  let hour = ("0" + dateTimeZone.getHours()).slice(-2);
  let mins = ("0" + dateTimeZone.getMinutes()).slice(-2);
  let currentTime = `${hour}:${mins}`;

  //convert to TimeZone
  function convertTimeZone(originalTime){
    let utcOriginalTime =
      originalTime.getTime() + originalTime.getTimezoneOffset() * 60000;
    let newDateTimeZone = new Date(utcOriginalTime + 3600000 * offset);
    return newDateTimeZone
  }

  return (
      <>
        <CityInput
          city={city}
          handleCityInput={handleCityInput}
          handleSubmitOnEnter={handleSubmitOnEnter}
        />

        {loading?<p>Loading</p>:
        <>
        <p>I am here wtf</p>
        <DisplaySky data={data}/>
        </>
        } 

      </>


        // {loading ? <p>Loading...</p> :
       // <DisplaySky 
      //    city={search}
      //    temp={data.main.temp}
      //   />
     //  }
    //      desc={data.weather[0].description}
    //      currentTime={currentTime} 
    //      clouds={data.clouds.all}
    //      dateTimeZone={dateTimeZone}
    //      convertTimeZone={convertTimeZone}
    //      lat={data.coord.lat}
    //      lon={data.coord.lon}
    //    />
    //    }
      // </>
  );
}

export default App;
