import React, { useEffect, useState } from "react";

function Moon ({dateTimeZone, sunsetTime,sunriseTime,cloudAmount}){

    const [moon,setMoon]= useState(false)

    useEffect(()=>{
        if (dateTimeZone >= sunsetTime|| dateTimeZone<= sunriseTime) {
            setMoon(true)
        }
    },[])

    //SunCalc 
    const SunCalc = require('suncalc');
    const moonData = SunCalc.getMoonIllumination(dateTimeZone);
    const moonPhase = moonData.phase

    const boxShadowPX=-Math.abs(moonPhase * 210) ;
    const opacityMoon =1- (cloudAmount * 0.01);

    const divStyle = {
        boxShadow: `inset ${boxShadowPX}px 0px rgba(241, 238, 225, ${opacityMoon})`,
      };

    return(
    <>
    {moon&&
    <div className="moon" style={divStyle}></div>
    }
    </>)
}

 export default Moon;
