     //Convert UNIX sunrise and sunset
     function unixToFormat(unixDate){
        let newDate = new Date(unixDate * 1000)
        let hour = ("0" + newDate.getHours()).slice(-2);
        let mins = ("0" + newDate.getMinutes()).slice(-2);
        let formattedDate = `${hour}:${mins}`;
        return formattedDate;
    }
    const sunriseFormated = unixToFormat(sunrise);
    const sunsetFormated = unixToFormat(sunset)

