import React, {useState, useEffect} from "react";
import CityInput from "./CityInput";
import DisplaySky from "./DisplaySky";
import '../css/App.css';



function App() {
  //Get city from Input
  const [city,setCity] = useState("");
  function handleCityInput(e){
    setCity(e.target.value)
  }

  //Get info on enter
  const [submit,setSubmit] = useState(false)
  function handleSubmitOnEnter(e){
    if (e.key === "Enter") {
      e.preventDefault();
      setSubmit(true)
    }
  }

  //Fetch API
  const API_KEY = process.env.REACT_APP_API_KEY 
  const URI = `http://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${API_KEY}`;
  const [data, setData] = useState([]);

  useEffect(() => {
      const fetchData = async () => {
      try {
          const response = await fetch(URI);
          const data = await response.json();
          setData(data);
      } catch (error) {
          console.error(error.message);
      }
      };
      fetchData();
  }, [URI]);

  //Time
  const time = new Date()
  //TimeZone
  const utc = time.getTime() + time.getTimezoneOffset() * 60000;
  const offset = data.timezone / 3600;
  const dateTimeZone = new Date(utc + 3600000 * offset);
  //Format Clock
  let hour = ("0" + dateTimeZone.getHours()).slice(-2);
  let mins = ("0" + dateTimeZone.getMinutes()).slice(-2);
  let currentTime = `${hour}:${mins}`;



  //Sky Color
  let skyBlueInitRGBA = [135, 206, 250, 1];
  let cloudAmount = Number(data.clouds.all);
  //Define individual RGBA values according to cloud amount
  skyBlueInitRGBA[1] -= cloudAmount;
  skyBlueInitRGBA[0] -= cloudAmount - cloudAmount / 2;
  skyBlueInitRGBA[2] -= cloudAmount;
  skyBlueInitRGBA[3] -= cloudAmount * 0.01;

  document.body.style = `background: ${skyBlueInitRGBA};`;
  return (
    <>
      {/* Input */}
      {!submit &&
      <CityInput
        city={city}
        handleCityInput={handleCityInput}
        handleSubmitOnEnter={handleSubmitOnEnter}
      />
      }

      {/* Display */}
      {submit && <DisplaySky 
        city={city}
        setSubmit={setSubmit}
        temp={data.main.temp}
        desc={data.weather[0].description}
        currentTime={currentTime} 
      />}

    </>
  );
}

export default App;
