import React, {useState, useEffect} from "react";
import CityInput from "./CityInput";
import DisplaySky from "./DisplaySky";
import '../css/App.css';


function App() {

  //Get city from Input
  const [city,setCity] = useState("");
  function handleCityInput(e){
    setCity(e.target.value)
  }

  //Get info on enter
  const [submit,setSubmit] = useState(false)
  function handleSubmitOnEnter(e){
    if (e.key === "Enter") {
      e.preventDefault();
      setSubmit(true)
    }
  }

  const API_KEY = process.env.REACT_APP_API_KEY 
  const URI = `http://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=ceb3c407c92ea3f7e0ea24125a3072df`;
  const [data, setData] = useState([]);
  const [searchURI, setSearchURI] = useState(URI);

  useEffect(() => {
      const fetchData = async () => {
      try {
          const response = await fetch(searchURI);
          const data = await response.json();
          setData(data);
      } catch (error) {
          console.error(error.message);
      }
      };
      fetchData();
  }, [searchURI]);

  return (
    <>


      {/* Input */}
      {!submit &&
      <CityInput
        city={city}
        handleCityInput={handleCityInput}
        handleSubmitOnEnter={handleSubmitOnEnter}
      />
      }

      {/* Display */}
      {submit && <DisplaySky 
        city={city}
        setSubmit={setSubmit}
      />}


    </>
  );
}

export default App;
