import React, { useState } from "react";

function Night ({sunrise,sunset,dateTimeZone,offset,convertTimeZone}){
    //Calculate Sunset UTC
    let sundown= new Date(sunset * 1000);
    const sundownTimeZone = convertTimeZone(sundown)

    //Calculate sunrise UTC
    let sunup = new Date(sunrise * 1000);
    const sunupTimeZone = convertTimeZone(sunup)

    //Make black on sunset and sunrise
    let sundownUTCgetTime = sundownTimeZone.getTime();
    let sunupUTCgetTime = sunupTimeZone.getTime();
    let dateTimeZoneGetTime = dateTimeZone.getTime();

    //Moon Toggle
    // const [night,setNight]=useState(false)

    if (dateTimeZoneGetTime >= sundownUTCgetTime || dateTimeZoneGetTime<= sunupUTCgetTime) {
      document.body.style.backgroundColor = "#2f3e46";
      // setNight(true)
    }

    return(
      <>
        {/* {night&& */}
        <div className="moon">
        </div>
        {/* } */}
      </>
    )
}

export default Night;
